using NAudio.Wave;
using NUnit.Framework;
using System;
using System.IO;

namespace NAudioTests.WaveStreams
{
    [TestFixture]
    public class AudioFileReaderTests
    {
        [Test]
        [Category("IntegrationTest")]
        public void CanBeDisposedMoreThanOnce()
        {
            var path = @"..\..\..\SampleData\Drums\closed-hat-trimmed.wav";
            if (!File.Exists(path))
                Assert.Ignore("test file not found");
            var reader = new AudioFileReader(path);
            reader.Dispose();
            Assert.DoesNotThrow(() => reader.Dispose());
        }

        [Test]
        public void UseMemoryStream()
        {
            // A short 'beep' WAV file
            var data = Convert.FromBase64String("");

            var stream = new MemoryStream(data);

            var reader = new AudioFileReader(stream, ".wav");
            reader.Dispose();
        }

        [Test]
        public void MemoryStreamFileExtMissingPeriod()
        {
            // A short 'beep' WAV file
            var data = Convert.FromBase64String("");

            var stream = new MemoryStream(data);

            Assert.Throws<ArgumentOutOfRangeException>(() => { var reader = new AudioFileReader(stream, "wav"); });
        }

        [Test]
        public void MemoryStreamFileExtMissing()
        {
            // A short 'beep' WAV file
            var data = Convert.FromBase64String("");

            var stream = new MemoryStream(data);

            Assert.Throws<ArgumentNullException>(() => { var reader = new AudioFileReader(stream, null); });
        }

        [Test]
        public void MemoryStreamMissing()
        {
            Assert.Throws<ArgumentNullException>(() => { var reader = new AudioFileReader(null, ".wav"); });
        }
    }
}
